import { useState } from "react";
import Input from "./Input";

const EmailInput = ({ type, id, isInvalid }) => {
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);
  const [emailInputIsInvalid, setEmailInputIsInvalid] = useState(false);

  const regEx = new RegExp(/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/gi);

  const enteredEmailIsValid = enteredEmail.test(regEx);

  console.log(enteredEmailIsValid);

  const emailInputChangeHandler = (e) => {
    setEnteredEmail(e.target.value);
    setEnteredEmailTouched(true);
    isInvalid(emailInputIsInvalid);
  };

  const emailInputBlurHandler = () => {
    setEnteredEmailTouched(true);
    isInvalid(!enteredEmailIsValid);
  };

  return (
    <>
      <input
        type={type}
        id={id}
        onChange={emailInputChangeHandler}
        onBlur={emailInputBlurHandler}
        value={enteredEmail}
      />
      {emailInputIsInvalid && (
        <p className="error-text">E-mail must be valid.</p>
      )}
    </>
  );
};

export default EmailInput;
