import EmailInput from "./EmailInput";
import NameInput from "./NameInput";

const BasicForm = (props) => {
  const submitHandler = (e) => {
    // setEnteredName("");
    // setEnteredNameTouched(false);
  };

  let nameInputIsInvalid = false;

  const checkNameValidation = (isInvalid) => {
    console.log(isInvalid + "asd");
    nameInputIsInvalid = isInvalid;
  };

  const nameInputClasses = nameInputIsInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className={nameInputClasses}>
          <label htmlFor="name">First Name</label>
          <NameInput type="text" id="name" isInvalid={checkNameValidation} />
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        {/* <EmailInput type="email" id="email" /> */}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
