import { useRouteLoaderData, json, redirect, defer } from 'react-router-dom';

import EventItem from '../components/EventItem';
import EventsList from '../components/EventsList';

export const EventDetailPage = () => {
	const data = useRouteLoaderData('event-detail');
	const event = data.event;

	return (
		<>
			<h1>EventDetail</h1>
			<EventItem event={event} />
			<EventsList events={events} />
		</>
	);
};

const loadEvents = async () => {
	const response = await fetch('http://localhost:8080/events');

	if (!response.ok) {
		throw json(
			{ message: 'Could not fetch events.' },
			{
				status: 500,
			},
		);
	} else {
		const resData = await response.json();

		return resData.events;
	}
};

export const loadEvent = async id => {
	const URL = `http://localhost:8080/events/${id}`;

	const res = await fetch(URL);

	if (!res.ok) {
		throw json(
			{ message: 'Could not fetch details for selected event.' },
			{
				status: 500,
			},
		);
	} else {
		const resData = await res.json();

		return resData.event;
	}
};

export const loader = async ({ req, params }) => {
	const id = params.id;

	return defer({
		event: loadEvent(id),
		events: loadEvents()
	})
};

export const action = async ({ params, request }) => {
	const eventId = params.id;

	const res = await fetch(`http://localhost:8080/events/${eventId}`, { method: request.method });

	if (!res.ok) {
		throw json({ message: 'Could not delete event.' }, { status: 500 });
	}

	return redirect('/events');
};
